const $ = new Env('IT之家')
$.userHash = 'senku_ithome_userHash'
const code = [0, 1, 2, 3, 256, 257, 258, 259, 512, 513, 514, 515, 768, 769, 770, 771]
$.arr = []


!(async () => {
    await enc()
    code.forEach(async(val) => {
        await sign(val)
    })
    await signinfo()
    await showmsg()
})()
.catch((e) => $.logErr(e))
.finally(() => $.done())



function sign(type) {
    return new Promise((resolve) => {
        const userHash = $.getdata($.userHash)
        const endt = $.endt
        const url = {url:`https://my.ruanmei.com/api/usersign/sign?userHash=${userHash}&type=${type}&endt=${endt}`,headers:{
            'Host': 'my.ruanmei.com',
            'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/71.0.3578.98 Safari/537.36',
        }}
        $.get(url, (err, resp, data) => {
            try {
                const res = JSON.parse(data)
                $.arr.push(res.ok)
            } catch (e) {
            $.logErr(e, resp)
            } finally {
            resolve()
            }
        })
    })
}

function signinfo() {
    return new Promise((resolve) => {
        const url = {url:`https://my.ruanmei.com/api/usersign/getsigninfo?userHash=${$.getdata($.userHash)}&type=0`,headers:{}}
        url.headers['User-Agent'] = 'Mozilla/5.0 (iPhone; CPU iPhone OS 14_0 like Mac OS X) AppleWebKit/605.1.15 (KHTML, like Gecko) Mobile/15E148 ithome/rmsdklevel2/day/7.32'
        $.get(url, (err, resp, data) => {
            try {
                $.signinfo = JSON.parse(data)
            } catch (e) {
            $.logErr(e, resp)
            } finally {
            resolve()
            }
        })
    })
}

function enc() {
    return new Promise((resolve) => {
        const date = `${$.time('yyyy-MM-dd HH:mm:ss')}`
        const url = {url:`https://tool.lmeee.com/jiami/cryptinter?mode=ECB&padding=zero&password=HCa%25Y%7C7%23&iv=&encode=hex&way=1&text=${date}&method=des`,headers:{
            'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/71.0.3578.98 Safari/537.36',
        }}
        $.post(url, (err, resp, data) => {
            try {
                $.endt = JSON.parse(data).d.r
            } catch (e) {

            } finally {
            resolve()
            }
        })
    })
}

function showmsg() {
    return new Promise((resolve) => {
        $.subt = `签到:${$.arr.includes(0) ? `今日已签到` : `签到成功`}`
        $.desc = []
        if ($.signinfo) {
            $.desc.push(`连续:${$.signinfo.cdays}天,累计:${$.signinfo.mdays}天,金币:${$.signinfo.totalcoin}个`)
        }
        $.msg($.name, $.subt, $.desc.join('\n'))
        resolve()
    })
}

